import React, { useState } from 'react';

// Helper function to generate auto-numbering
const generateNumber = (prefix, index) => `${prefix}${index + 1}`;

const QuestionItem = ({ question, onDelete, onAddChild, onUpdate, numbering }) => {
  return (
    <div style={{ marginLeft: `${question.depth * 20}px` }}>
      <h4>{numbering}</h4>
      <input
        type="text"
        value={question.text}
        onChange={(e) => onUpdate(question.id, 'text', e.target.value)}
        placeholder="Enter question"
      />
      <select
        value={question.type}
        onChange={(e) => onUpdate(question.id, 'type', e.target.value)}
      >
        <option value="Short Answer">Short Answer</option>
        <option value="True/False">True/False</option>
      </select>

      {question.type === 'True/False' && (
        <>
          <select
            value={question.answer}
            onChange={(e) => onUpdate(question.id, 'answer', e.target.value)}
          >
            <option value="">Select Answer</option>
            <option value="True">True</option>
            <option value="False">False</option>
          </select>
          {/* Show "Add Child" option only if the answer is "True" */}
          {question.answer === 'True' && (
            <button onClick={() => onAddChild(question.id)}>Add Child Question</button>
          )}
        </>
      )}

      <button onClick={() => onDelete(question.id)}>Delete</button>
    </div>
  );
};

// Component to display the submitted questions in hierarchical format
const QuestionDisplay = ({ questions }) => {
  // Recursive function to display the questions
  const renderQuestions = (items, numbering = '') => {
    return items.map((q, index) => {
      const currentNumbering = generateNumber(numbering ? numbering + '.' : 'Q', index);
      return (
        <div key={q.id} style={{ marginLeft: `${q.depth * 20}px` }}>
          <strong>{currentNumbering}</strong> {q.text} ({q.type}){' '}
          {q.type === 'True/False' && `Answer: ${q.answer}`}
          {q.children.length > 0 && renderQuestions(q.children, currentNumbering)}
        </div>
      );
    });
  };

  return (
    <div>
      <h3>Submitted Questions</h3>
      {renderQuestions(questions)}
    </div>
  );
};

const DynamicForm = () => {
  const [questions, setQuestions] = useState([]);
  const [submittedQuestions, setSubmittedQuestions] = useState(null);

  // Generate unique IDs for questions
  const generateId = () => '_' + Math.random().toString(36).substr(2, 9);

  // Add new parent question
  const addQuestion = () => {
    const newQuestion = {
      id: generateId(),
      text: '',
      type: 'Short Answer',
      answer: '',
      depth: 0,
      children: [],
    };
    setQuestions([...questions, newQuestion]);
  };

  // Recursive function to update questions
  const updateQuestion = (id, field, value, items = questions) => {
    return items.map((q) => {
      if (q.id === id) {
        return { ...q, [field]: value };
      } else if (q.children.length) {
        return { ...q, children: updateQuestion(id, field, value, q.children) };
      }
      return q;
    });
  };

  // Add child question
  const addChildQuestion = (id) => {
    const updatedQuestions = questions.map((q) => {
      if (q.id === id) {
        const newChild = {
          id: generateId(),
          text: '',
          type: 'Short Answer',
          answer: '',
          depth: q.depth + 1,
          children: [],
        };
        return { ...q, children: [...q.children, newChild] };
      }
      return q;
    });
    setQuestions(updatedQuestions);
  };

  // Delete question and its children recursively
  const deleteQuestion = (id, items = questions) => {
    return items.filter((q) => {
      if (q.id === id) return false;
      if (q.children.length) {
        q.children = deleteQuestion(id, q.children);
      }
      return true;
    });
  };

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    setSubmittedQuestions(questions);
  };

  // Recursive function to display the hierarchical view in form
  const renderQuestions = (items, numbering = '') => {
    return items.map((q, index) => {
      const currentNumbering = generateNumber(numbering ? numbering + '.' : 'Q', index);
      return (
        <div key={q.id}>
          <QuestionItem
            question={q}
            onDelete={(id) => setQuestions(deleteQuestion(id))}
            onAddChild={(id) => addChildQuestion(id)}
            onUpdate={(id, field, value) => setQuestions(updateQuestion(id, field, value))}
            numbering={currentNumbering}
          />
          {q.children.length > 0 && renderQuestions(q.children, currentNumbering)}
        </div>
      );
    });
  };

  return (
    <div>
      <h1>Dynamic Question Form</h1>
      {submittedQuestions ? (
        <QuestionDisplay questions={submittedQuestions} />
      ) : (
        <>
          <button onClick={addQuestion}>Add New Question</button>
          <form onSubmit={handleSubmit}>
            {renderQuestions(questions)}
            <button type="submit">Submit</button>
          </form>
        </>
      )}
    </div>
  );
};

export default DynamicForm;
